import { styled } from '@mui/system';

export const myTheme = createTheme({
  palette:{
      primary:{
          main: teal[500]
      },
      secondary:{
          main: '#fafafa'
      },
      tertiary:{
        main: '#f9f9f9'
      },
      warning:{
          main: yellow[600]
      },
      info:{
          main: green[600]
      },
      text:{
        main: '#cccccc' 
      }
  }
})

export const MainCardsDiv = styled('div')({
    marginTop:15,
    marginLeft:15,
    padding:10, 
    textAlign: 'center',
});

export const classCss = {
  divMainPannel: {
    flexGrow: 1,
	margin: 20,
  },
  paper: {
    padding: 20,
	margin:10,
    textAlign: "center",
    backgroundColor: theme.palette.primary.main,
    color: "white",
	width: '330px',
	height: '200px',
  },
  box:{
	margin: 20,
	backgroundColor: "#fafafa",
    color: "gray",
	width: '400px',
	height: '300px',
  }
};
